import React from 'react';
import Story from "./Story";

import './Stories.css';

const COLUMNS = {
    title: {
        width: '40%',
        label: 'Title'
    },
    author: {
        width: '30%',
        label: 'Author'
    },
    comments: {
        width: '10%',
        label: 'Commnets'
    },
    points: {
        width: '10%',
        label: 'Points'
    },
    archive: {
        width: '10%',
    }
};

const Stories = ({ stories }) => {
    return (
        <div className="stories">
            <div className="stories-header">
                {Object.keys(COLUMNS).map(key => (
                    <span style={{ width: COLUMNS[key].width }}>
                        {COLUMNS[key].label}
                    </span>
                ))}
            </div>
            {stories.map(story => (
                <Story
                    key={story.objectID}
                    story={story}
                    columns={COLUMNS}
                />
            ))}
        </div>
    );
}

export default Stories;
