import React, { Component } from 'react';
import connect from 'redux';
import CreateProduct from "../components/ProductForm";
import { capitalize } from "../utils/textTransform";
import { createProduct } from "../actions/actionsTypes";
import { hasCreatePermission } from "../utils/hasPermission";
import Title from "../components/Title";

export class CreateProductContainer extends Component {
    state = {
        name: '',
        price: 0,
        currency: ''
    };

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value })
    }

    _rollTheDice = () => {
        Math.floor(100 + Math.random() * 900)
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const { name, currency, price } = this.state;
        const createProductData = {
            id: this._rollTheDice(),
            name: capitalize(name),
            price: parseInt(price, 10),
            currency: currency.toUpperCase()
        }

        this.props.handleSubmitForm(createProductData);
        this.handleClearForm(e);

    }
    handleClearForm = event => {
        event.preventDefault()
        this.setState({ name: '', price: 0, currency: '' })
    }
    render() {
        const { name, currency, price } = this.state;

        return (
            <div>
                {hasCreatePermission(this.props.permissions) ? (
                    [
                        <Title key="title">Create New Product</Title>,
                        <CreateProduct
                            key="createProduct"
                            {...this.state}
                            handleSubmit={this.handleSubmit}
                            handleChange={this.handleChange}
                        />
                    ]
                ) : (
                        <p>"You don't have the CREATE permission"</p>
                    )}
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    permissions: state.permissions
})

const mapDispathToProps = (dispatch) => ({
    handleSubmitForm(payload) {
        dispatch(createProduct(payload))
    }
})

export default connect(mapStateToProps, mapDispathToProps)(CreateProductContainer)
