import { call, put } from "@redux-saga/core/effects"
import { addUser } from "../api/loginApi";
import { doRegister } from "../actions/userAction";


function* registerUser({ userData }) {
    try {
        const result = yield call(addUser, userData);
        yield put(doRegister(result))
    } catch (error) {
        yield put(error)
    }
}

export { registerUser };
