import axios from 'axios'
import { API_URL } from '../api';
import { GET_PERMISSION, UPDATE_PERMICSSION } from './actionsTypes';

const getPermissionsSuccess = (payload) => ({
    type: GET_PERMISSION,
    payload
});

export function getPermissions() {
    return dispatch => {
        axios.get(`${API_URL}/permissions`)
            .then(response => dispatch(getPermissionsSuccess(response.data)))
            .catch(error => console.error('getPermissions axios error', error))
    }
};

const updatePermissionsSuccess = (permissions) => ({
    type: UPDATE_PERMICSSION,
    payload: permissions.options
});


export function updatePermissions(payload) {
    const permissions = [];
    const id = 1;

    Object.keys(payload).forEach(permission => {
        if (payload[permission]) {
            permissions.push(permission.toUpperCase());
        }
    });

    const newPermissions = {
        id,
        options: permissions
    };

    return dispatch => {
        axios.put(`${API_URL}/permissions/${payload.id}`, newPermissions)
            .then(response => dispatch(updatePermissionsSuccess(response.data)))
            .catch(error => console.error('updatePermissions axios error', error))
    }
};
