import React from 'react';
import Story from "./Story";

import './Stories.css';
import StoryHeader from "./StoryHeader";

const COLUMNS = {
    title: {
        width: '40%',
        label: 'Title'
    },
    author: {
        width: '30%',
        label: 'Author'
    },
    comments: {
        width: '10%',
        label: 'Commnets'
    },
    points: {
        width: '10%',
        label: 'Points'
    },
    archive: {
        width: '10%',
    }
};

const Stories = ({ stories, onArchive }) => {
    return (
        <div className="stories">
            <StoryHeader columns={COLUMNS} />
            {stories.map(story => (
                <Story
                    key={story.objectID}
                    story={story}
                    columns={COLUMNS}
                    onArchive={onArchive}
                />
            ))}
        </div>
    );
}

export default Stories;
