import React, { Component } from 'react';
import { connect } from 'react-redux';
import ProductForm from "../components/ProductForm";
import { capitalize } from "../utils/textTransform";
import { updateProduct } from "../actions/actionsTypes";

export class UpdateProductContainer extends Component {
    state = {
        name: '',
        price: 0,
        currency: ''
    };

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const { name, currency, price } = this.state;
        const updateProductsData = {
            id: this.props.id,
            name: capitalize(name),
            price: parseInt(price, 10),
            currency: currency.toUpperCase()
        }

        this.props.handleSubmitForm(updateProductsData);
        this.handleClearForm(e);

    }
    handleClearForm = event => {
        event.preventDefault()
        this.props.onUpdateSuccess()
    }
    componentDidMount() {
        const { name, currency, price } = this.props;
        this.setState({ name, currency, price })
    }
    render() {
        const { name, currency, price } = this.state;

        return (
            <ProductForm
                name={name}
                price={price}
                currency={currency}
                handleSubmit={this.handleSubmit}
                handleChange={this.handleChange}
            />
        )
    }
}

const mapStateToProps = (state, ownProps) => {
    return ownProps;
}

const mapDispathToProps = (dispatch) => ({
    handleSubmitForm(payload) {
        dispatch(updateProduct(payload))
    }
})

export default connect(mapStateToProps, mapDispathToProps)(UpdateProductContainer)
