import { GET_PRODUCTS, CREATE_PRODUCTS, UPDATE_PRODUCTS, DELETE_PRODUCTS } from "../actions/actionsTypes";

const initialState = [];

export default (state = initialState, { type, payload }) => {
    switch (type) {

        case GET_PRODUCTS:
            return [...state, ...payload]
        case CREATE_PRODUCTS:
            return [...state, ...payload]
        case UPDATE_PRODUCTS:
            const indexOfUpdatedProduct = state.findIndex(product => product.id === payload.id);
            state.splice(indexOfUpdatedProduct, 1);
            state.splice(indexOfUpdatedProduct, 0, payload);
            return [...state]
        case DELETE_PRODUCTS:
            const indexOfDeletedProduct = state.findIndex(product => product.id === payload.id);
            state.splice(indexOfDeletedProduct, 1);
            return [...state]

        default:
            return state
    }
}
