import { STORY_ADD } from "../constants/actionTypes";

const initialState = {
    stories: [],
    error: null
};
const applyAddStories = (stories) => ({ stories, error: null });

const storyReduer = (state = initialState, { type, stories }) => {
    switch (type) {
        case STORY_ADD:
            return applyAddStories(stories)
        default:
            return state
    }
}

export default storyReduer;
